import './App.css';
import React, { useState, useEffect } from 'react';
import 'rbx/index.css';
import { Container, Title, Box, Media, Image, Content, Footer, Dropdown, Button, Icon } from 'rbx';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { library } from '@fortawesome/fontawesome-svg-core';
import { fab } from '@fortawesome/free-brands-svg-icons';
import { faCheckSquare, faCoffee} from '@fortawesome/free-solid-svg-icons';

library.add(fab, faCheckSquare, faCoffee, faAngleDown)

const Banner = ({ title }) => (
  <Title style = {{color: 'white', paddingTop:'40px', textAlign:"center"}}>{ title }</Title>
);

const TweetList = ({ tweets }) => (
  <div>
    {/* { courses.map(course => <TwitterTweetEmbed tweetId={parseInt(course.id)}/>) } */}
    { tweets.map(tweet => <Tweet key={tweet.id} tweet={ tweet } />) }
  </div>
);

// pass in
const Tweet = ({ tweet }) => (
  <Box style = {{backgroundColor: '##F0F0F0'}}>
  <Media>
    <Media.Item align="left">
      <Image.Container size={64}>
        <FontAwesomeIcon icon={["fab", "twitter-square"]} fontSize={'50px'} />
      </Image.Container>
    </Media.Item>
    <Media.Item>
      <Content>
        <p>
          {tweet.text}
        </p>
      </Content>
    </Media.Item>
  </Media>
</Box>
);

const App = () => {
  // const [stockName, setStockName] = useState({ name: [] });
  let stockName = 'AAPL';
  const [stockList, setStockList] = useState({ data: [] });
  const url = 'http://localhost:8081/stocks/'+stockName;

  useEffect(() => {
    const fetchStockList = async () => {
      const response = await fetch(url);
      if (!response.ok) throw response;
      const json = await response.json();
      setStockList(json);
    }
    fetchStockList();
  }, [])

  if (stockList.data) {
    return (
      <div style = {{backgroundColor: '#1DA1F2'}}>
      <Container fluid>


  <Dropdown>
    <Dropdown.Trigger>
      <Button>
        <span>Dropdown button</span>
        <Icon size="small">
          <FontAwesomeIcon icon={faAngleDown} />
        </Icon>
      </Button>
    </Dropdown.Trigger>
    <Dropdown.Menu>
      <Dropdown.Content>
        <Dropdown.Item>Dropdown item</Dropdown.Item>
        <Dropdown.Item>Other dropdown item</Dropdown.Item>
        <Dropdown.Item active>Active dropdown item</Dropdown.Item>
        <Dropdown.Item>Other dropdown item</Dropdown.Item>
        <Dropdown.Divider />
        <Dropdown.Item>With a divider</Dropdown.Item>
      </Dropdown.Content>
    </Dropdown.Menu>
    </Dropdown>



        <TweetList tweets={ stockList.data } />
        <Footer style = {{backgroundColor: '#1DA1F2'}}></Footer>
      </Container>
      </div>

    );
  }
  else {
    return (<div>Loading...</div>);
  }
};

export default App;
